import React, { useState, useEffect } from "react";
import "../styles/Challenges.css";
import Navbar from "./navbar";
import Footer from "./footer";

const Challenges = () => {
  const [challenges, setChallenges] = useState([]);

  useEffect(() => {
    const fetchChallenges = async () => {
      try {
        const response = await fetch(
          "http://localhost:8000/challenges/challenges/"
        );
        const data = await response.json();
        setChallenges(data.challenges);
      } catch (error) {
        console.error("Error fetching challenges:", error);
      }
    };

    fetchChallenges();
  }, []);

  return (
    <>
        <Navbar />
      <div className="challenges-page">
        <h1>Fitness Challenges</h1>
        <div className="challenge-list">
          {challenges.length > 0 ? (
            challenges.map((challenge, index) => (
              <div key={index} className="challenge-item">
                <h2>{challenge.title}</h2>
                <p>
                  <strong>Description:</strong> {challenge.description}
                </p>
                <p>
                  <strong>Goal:</strong> {challenge.goal}
                </p>
                <p>
                  <strong>Tracking Method:</strong> {challenge.tracking_method}
                </p>
                <p>
                  <strong>Reward:</strong> {challenge.reward}
                </p>
                <p>
                  <strong>Start Date:</strong> {challenge.start_date}
                </p>
                <p>
                  <strong>End Date:</strong> {challenge.end_date}
                </p>
              </div>
            ))
          ) : (
            <p>Loading challenges...</p>
          )}
        </div>
      </div>

      <Footer />
    </>
  );
};

export default Challenges;
