import React, { useState, useEffect } from "react";
import "../styles/Challenges.css";
import Navbar from "./navbar";
import Footer from "./footer";

const Challenges = () => {
  const [challenges, setChallenges] = useState([]);
  const [filteredChallenges, setFilteredChallenges] = useState([]);
  const [challengeType, setChallengeType] = useState("all"); // Filter state
  const [currentIndex, setCurrentIndex] = useState(0); // For slider
  

  useEffect(() => {
    const fetchChallenges = async () => {
      try {
        const response = await fetch(
          "http://localhost:8000/challenges/challenges/"
        );
        const data = await response.json();
        setChallenges(data.challenges);
        setFilteredChallenges(data.challenges); // Initially show all challenges
      } catch (error) {
        console.error("Error fetching challenges:", error);
      }
    };

    fetchChallenges();
  }, []);

  // Filter challenges by type
  useEffect(() => {
    if (challengeType === "all") {
      setFilteredChallenges(challenges);
    } else {
      const filtered = challenges.filter(
        (challenge) => challenge.challenge_type === challengeType
      );
      setFilteredChallenges(filtered);
    }
    setCurrentIndex(0); // Reset slider index when filter changes
  }, [challengeType, challenges]);

  // Handle type change
  const handleTypeChange = (e) => {
    setChallengeType(e.target.value);
  };

  // Slider functions
  const handlePrev = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex > 0 ? prevIndex - 1 : filteredChallenges.length - 1
    );
  };

  const handleNext = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex < filteredChallenges.length - 1 ? prevIndex + 1 : 0
    );
  };

  const openModal = (challenge) => {
    setSelectedChallenge(challenge);
  };

  const closeModal = () => {
    setSelectedChallenge(null);
  };

  // Separate weekly and monthly challenges
  const weeklyChallenges = filteredChallenges.filter(
    (challenge) => challenge.challenge_type === "weekly"
  );
  const monthlyChallenges = filteredChallenges.filter(
    (challenge) => challenge.challenge_type === "monthly"
  );

  return (
    <>
      <Navbar />
      <div className="challenges-page">
        <div className="challenges-title">
          <h1 className="challenges-title-text">Fitness Challenges</h1>
        </div>

        {/* Dropdown to select challenge type */}
        <div className="filter-container">
          <label htmlFor="challengeType">Select Program Type:</label>
          <select
            className="choose-type "
            id="challengeType"
            value={challengeType}
            onChange={handleTypeChange}
          >
            <option value="all">All Programs</option>
            <option value="weekly">Weekly Programs</option>
            <option value="monthly">Monthly Programs</option>
          </select>
        </div>

        {/* Weekly Challenges Section */}
        <div className="challenge-section">
          <h2 className="challenges-type-title">Weekly Challenges</h2>
          <div className="slider-container">
            <button className="slider-btn" onClick={handlePrev}>
              &#9664;
            </button>
            <div className="challenge-slider">
              {challenges
                .filter((challenge) => challenge.challenge_type === "weekly")
                .map((challenge, index) => (
                  <div
                    key={index}
                    className="challenge-item"
                    onClick={() => openModal(challenge)}
                  >
                    <img
                      src={challenge.challenge_img}
                      alt={challenge.title}
                      className="challenge-img"
                    />
                    <h3>{challenge.title}</h3>
                  </div>
                ))}
            </div>
            <button className="slider-btn" onClick={handleNext}>
              &#9654;
            </button>
          </div>
        </div>

        {/* Monthly Challenges Section */}
        <div className="challenge-section">
          <h2 className="challenges-type-title">Monthly Challenges</h2>
          <div className="slider-container">
            <button className="slider-btn" onClick={handlePrev}>
              &#9664;
            </button>
            <div className="challenge-slider">
              {challenges
                .filter((challenge) => challenge.challenge_type === "monthly")
                .map((challenge, index) => (
                  <div
                    key={index}
                    className="challenge-item"
                    onClick={() => openModal(challenge)}
                  >
                    <img
                      src={challenge.challenge_img}
                      alt={challenge.title}
                      className="challenge-img"
                    />
                    <h3>{challenge.title}</h3>
                  </div>
                ))}
            </div>
            <button className="slider-btn" onClick={handleNext}>
              &#9654;
            </button>
          </div>
        </div>

        {/* Modal for challenge details */}
        {selectedChallenge && (
          <div className="challenge-modal">
            <div className="modal-content">
              <button className="close-btn" onClick={closeModal}>
                &times;
              </button>
              <h3>{selectedChallenge.title}</h3>
              <p>
                <strong>Description:</strong> {selectedChallenge.description}
              </p>
              <p>
                <strong>Goal:</strong> {selectedChallenge.goal}
              </p>
              <p>
                <strong>Tracking Method:</strong>{" "}
                {selectedChallenge.tracking_method}
              </p>
              <p>
                <strong>Reward:</strong> {selectedChallenge.reward}
              </p>
              <p>
                <strong>Start Date:</strong> {selectedChallenge.start_date}
              </p>
              <p>
                <strong>End Date:</strong> {selectedChallenge.end_date}
              </p>
              <button className="get-btn">Get Challenge</button>
            </div>
          </div>
        )}
      </div>
      <Footer />
    </>
  );
};

export default Challenges;
