import React, { useState, useEffect } from "react";
import "../styles/Dashboard.css";

function Dashboard() {
  const [activeOption, setActiveOption] = useState("profile"); // Active section (profile by default)
  const [user, setUser] = useState({
    user_name: "",
    weight: "",
    height: "",
    profile_pic: "",
  });

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (storedUser) {
      setUser(storedUser);
    }
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUser((prevUser) => ({ ...prevUser, [name]: value }));
  };

  const handleFileChange = (e) => {
    setUser((prevUser) => ({ ...prevUser, profile_pic: e.target.files[0] }));
  };

  const handleSave = () => {
    // Save changes to backend or localStorage
    localStorage.setItem("user", JSON.stringify(user));
    alert("Profile updated!");
  };

  return (
    <div className="dashboard-container">
      <div className="sidebar">
        <ul>
          <li onClick={() => setActiveOption("profile")}>Your Profile</li>
          <li onClick={() => setActiveOption("settings")}>Settings</li>
        </ul>
      </div>

      <div className="main-content">
        {activeOption === "profile" && (
          <div className="profile-section">
            <h2>Edit Your Profile</h2>
            <label>Name:</label>
            <input
              type="text"
              name="user_name"
              value={user.user_name}
              onChange={handleInputChange}
            />
            <label>Weight:</label>
            <input
              type="number"
              name="weight"
              value={user.weight}
              onChange={handleInputChange}
            />
            <label>Height:</label>
            <input
              type="number"
              name="height"
              value={user.height}
              onChange={handleInputChange}
            />
            <label>Profile Picture:</label>
            <input type="file" onChange={handleFileChange} />
            <button onClick={handleSave}>Save Changes</button>
          </div>
        )}
        {activeOption === "settings" && <div>Settings Section (Coming Soon)</div>}
      </div>
    </div>
  );
}

export default Dashboard;
