import React, { useState, useEffect } from "react";
import "../styles/Dashboard.css";

function Dashboard() {
  const [activeOption, setActiveOption] = useState("profile"); // Active section (profile by default)
  const [user, setUser] = useState({
    user_name: "",
    weight: "",
    height: "",
    profile_pic: "",
  });

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (storedUser) {
      setUser(storedUser);
    }
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUser((prevUser) => ({ ...prevUser, [name]: value }));
  };

  const handleFileChange = (e) => {
    setUser((prevUser) => ({ ...prevUser, profile_pic: e.target.files[0] }));
  };

  const handleSave = async () => {
    // Create FormData object to handle both file and text data
    const formData = new FormData();
    formData.append("user_name", user.user_name);
    formData.append("id", localStorage.getItem);
    formData.append("weight", user.weight);
    formData.append("height", user.height);
    if (user.profile_pic) {
      formData.append("profile_pic", user.profile_pic); // Handle file upload
    }

    try {
      const response = await fetch(
        `http://localhost:8000/account/update-profile/`,
        {
          method: "PUT", // Use PUT for updates
          body: formData, // Send FormData
        }
      );

      if (response.ok) {
        const updatedUser = await response.json();
        // Update localStorage with the updated user data
        localStorage.setItem("user", JSON.stringify(updatedUser));
        setUser(updatedUser);
        alert("Profile updated successfully!");
      } else {
        const errorData = await response.json();
        alert(errorData.error || "Failed to update profile.");
      }
    } catch (error) {
      console.error("Error updating profile:", error);
      alert("An error occurred. Please try again.");
    }
  };

  return (
    <div className="dashboard-container">
      <div className="sidebar">
        <ul>
          <li onClick={() => setActiveOption("profile")}>Your Profile</li>
          <li onClick={() => setActiveOption("settings")}>Settings</li>
        </ul>
      </div>

      <div className="main-content">
        {activeOption === "profile" && (
          <div className="profile-section">
            <h2>Edit Your Profile</h2>
            <label>Name:</label>
            <input
              type="text"
              name="user_name"
              value={user.user_name}
              onChange={handleInputChange}
            />
            <label>Weight:</label>
            <input
              type="number"
              name="weight"
              value={user.weight}
              onChange={handleInputChange}
            />
            <label>Height:</label>
            <input
              type="number"
              name="height"
              value={user.height}
              onChange={handleInputChange}
            />
            <label>Profile Picture:</label>
            <input type="file" onChange={handleFileChange} />
            <button onClick={handleSave}>Save Changes</button>
          </div>
        )}
        {activeOption === "settings" && (
          <div>Settings Section (Coming Soon)</div>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
