import React, { useState, useEffect } from "react";
import Navbar from "./navbar";
import Footer from "./footer";
import "./Challenges.css"; // Import your CSS file

const Challenges = () => {
  const [challenges, setChallenges] = useState([]);
  const [filteredChallenges, setFilteredChallenges] = useState([]);
  const [challengeType, setChallengeType] = useState("all"); // To filter by type (all, weekly, monthly)
  const [currentIndex, setCurrentIndex] = useState(0); // For slider navigation

  useEffect(() => {
    // Fetch challenges from the backend API
    const fetchChallenges = async () => {
      const response = await fetch("/api/challenges/");
      const data = await response.json();
      setChallenges(data);
      setFilteredChallenges(data); // Initially, show all challenges
    };

    fetchChallenges();
  }, []);

  // Filter challenges based on the selected challenge type
  useEffect(() => {
    if (challengeType === "all") {
      setFilteredChallenges(challenges);
    } else {
      const filtered = challenges.filter(
        (challenge) => challenge.challenge_type === challengeType
      );
      setFilteredChallenges(filtered);
    }
  }, [challengeType, challenges]);

  // Handle challenge type selection
  const handleTypeChange = (e) => {
    setChallengeType(e.target.value);
    setCurrentIndex(0); // Reset slider to the first challenge
  };

  // Slider navigation functions
  const handlePrev = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex > 0 ? prevIndex - 1 : filteredChallenges.length - 1
    );
  };

  const handleNext = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex < filteredChallenges.length - 1 ? prevIndex + 1 : 0
    );
  };

  // Separate challenges into weekly and monthly for display
  const weeklyChallenges = filteredChallenges.filter(
    (challenge) => challenge.challenge_type === "weekly"
  );
  const monthlyChallenges = filteredChallenges.filter(
    (challenge) => challenge.challenge_type === "monthly"
  );

  return (
    <>
      <div className="challenges-page">
        <h1>Fitness Challenges</h1>

        {/* Dropdown to choose challenge type */}
        <select value={challengeType} onChange={handleTypeChange}>
          <option value="all">All Programs</option>
          <option value="weekly">Weekly Programs</option>
          <option value="monthly">Monthly Programs</option>
        </select>

        {/* Weekly Challenges */}
        {challengeType === "all" || challengeType === "weekly" ? (
          <div>
            <h2>Weekly Challenges</h2>
            <div className="slider-container">
              <button className="slider-btn" onClick={handlePrev}>
                &#9664;
              </button>
              <div className="challenge-slider">
                {weeklyChallenges.length > 0 ? (
                  <div className="challenge-item">
                    <h2>{weeklyChallenges[currentIndex]?.title}</h2>
                    <p>
                      <strong>Goal:</strong>{" "}
                      {weeklyChallenges[currentIndex]?.goal}
                    </p>
                    <p>
                      <strong>Tracking:</strong>{" "}
                      {weeklyChallenges[currentIndex]?.tracking_method}
                    </p>
                    <p>
                      <strong>Reward:</strong>{" "}
                      {weeklyChallenges[currentIndex]?.reward}
                    </p>
                  </div>
                ) : (
                  <p>No weekly challenges available.</p>
                )}
              </div>
              <button className="slider-btn" onClick={handleNext}>
                &#9654;
              </button>
            </div>
          </div>
        ) : null}

        {/* Monthly Challenges */}
        {challengeType === "all" || challengeType === "monthly" ? (
          <div>
            <h2>Monthly Challenges</h2>
            <div className="slider-container">
              <button className="slider-btn" onClick={handlePrev}>
                &#9664;
              </button>
              <div className="challenge-slider">
                {monthlyChallenges.length > 0 ? (
                  <div className="challenge-item">
                    <h2>{monthlyChallenges[currentIndex]?.title}</h2>
                    <p>
                      <strong>Goal:</strong>{" "}
                      {monthlyChallenges[currentIndex]?.goal}
                    </p>
                    <p>
                      <strong>Tracking:</strong>{" "}
                      {monthlyChallenges[currentIndex]?.tracking_method}
                    </p>
                    <p>
                      <strong>Reward:</strong>{" "}
                      {monthlyChallenges[currentIndex]?.reward}
                    </p>
                  </div>
                ) : (
                  <p>No monthly challenges available.</p>
                )}
              </div>
              <button className="slider-btn" onClick={handleNext}>
                &#9654;
              </button>
            </div>
          </div>
        ) : null}
      </div>

      <Footer />
    </>
  );
};

export default Challenges;
